    console.log("ðŸŸ¡ LocalStorage'dan gÃ¶tÃ¼rÃ¼lÉ™n dil:", savedLang);

    if (!savedLang) {
        savedLang = 'az';
        localStorage.setItem('selectedLanguage', savedLang);
    }

    changeWebsiteLanguage(savedLang);
